package even;
import java.util.*;
public class EvenOdd {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println(System.in);
		Scanner sc=new Scanner(System.in);
		int x=sc.nextInt();
		if(x%2==0)
		{
			System.out.println("Even");
		}
		else {
			System.out.println("Odd");
		}
	}
}
